CREATE OR REPLACE VIEW TIETOOMAISUUS_JARJESTELMA_NIMI
            (ID,TIETOJARJESTELMA_ID,PRIMAARIKAYTTOTARVE,SEKUNDAAR_KAYTTOTARPEET,KAYTTAJAT,MALLINNUSTAPA,KAYTTOFORMAATIT,HISTORIATIEDOT,KATTAVUUS_JA_LAATU,METATIEDOT,MUUTOSTIEDOT,SAATAVUUS,HENKILOTIEDOT,EHEYS,AVOIMUUS,IMMATERIAALIOIKEUDET,YKSILOIVAT_TUNNISTEET,PYSYVYYS,DOKUMENTAATIO,ELINKAARI,ALUSTOJEN_TYYP_JA_SIJ,RIVILUOTUPVM,RIVIMUOKATTUPVM,RIVIMUOKKAAJATUNNUS,NIMI,KUVAUS,JARJESTELMAN_NIMI)
AS
select tietoomaisuus."ID",tietoomaisuus."TIETOJARJESTELMA_ID",tietoomaisuus."PRIMAARIKAYTTOTARVE",tietoomaisuus."SEKUNDAAR_KAYTTOTARPEET",tietoomaisuus."KAYTTAJAT",tietoomaisuus."MALLINNUSTAPA",tietoomaisuus."KAYTTOFORMAATIT",tietoomaisuus."HISTORIATIEDOT",tietoomaisuus."KATTAVUUS_JA_LAATU",tietoomaisuus."METATIEDOT",tietoomaisuus."MUUTOSTIEDOT",tietoomaisuus."SAATAVUUS",tietoomaisuus."HENKILOTIEDOT",tietoomaisuus."EHEYS",tietoomaisuus."AVOIMUUS",tietoomaisuus."IMMATERIAALIOIKEUDET",tietoomaisuus."YKSILOIVAT_TUNNISTEET",tietoomaisuus."PYSYVYYS",tietoomaisuus."DOKUMENTAATIO",tietoomaisuus."ELINKAARI",tietoomaisuus."ALUSTOJEN_TYYP_JA_SIJ",tietoomaisuus."RIVILUOTUPVM",tietoomaisuus."RIVIMUOKATTUPVM",tietoomaisuus."RIVIMUOKKAAJATUNNUS",tietoomaisuus."NIMI",tietoomaisuus."KUVAUS", tietojarjestelmasalkku.jarjestelman_nimi
from tietoomaisuus
         join tietojarjestelmasalkku on (tietoomaisuus.tietojarjestelma_id = tietojarjestelmasalkku.tietojarjestelmatunnus);